$(document).ready(function(){$("#search_submit").click(function(n){n.preventDefault();$("#search_form").submit()});$.get("/Order/ItemsInCart",function(n){$("#cart_badge").text(n)});$(".addToCartBtn").click(function(n){n.preventDefault();var t=$(this).val();$.post("/Order/AddToCart",{eventDateId:t},function(n){$("#cart_badge").text(n)})})});